jQuery(document).ready(function($) {    $(document).on('click', '.front_button', function() {        var post_data = $(this).attr('id').split('-'),            post_id = post_data[1];        window.send_to_editor = function(html) {            imgurl = $(html).attr('href');            if( imgurl === '' || typeof imgurl === 'undefined' ) {                imgurl = $(html).attr('src');            }            var img = $('<img>');            img.attr('src', imgurl);            img.attr('alt', $('img', html).attr('title'));            img.attr('width', '140'), span = $('<span>');            span.attr('class', 'img_delete');            span.attr('id', 'front-' + post_id);            $( '.front_image_preview' ).html( '' ).show();            $( '#front_image' ).val( imgurl );            img.appendTo( '.front_image_preview' );            span.appendTo( '.front_image_preview' );            tb_remove()        }    });    $(document).on('click', '.back_button', function() {        var post_data = $(this).attr('id').split('-'),            post_id = post_data[1];        window.send_to_editor = function(html) {            imgurl = $(html).attr('href');            if( imgurl === '' || typeof imgurl === 'undefined' ){                imgurl = $(html).attr('src');            }            var img = $('<img>');            img.attr('src', imgurl);            img.attr('alt', $('img', html).attr('title'));            img.attr('width', '140');            span = $('<span>');            span.attr('class', 'img_delete');            span.attr('id', 'back-' + post_id);            $('.back_image_preview').html('').show();            $('#back_image').val(imgurl);            img.appendTo('.back_image_preview');            span.appendTo('.back_image_preview');            tb_remove()        }    });    $(document).on('click', '.img_delete', function() {        var post_data = $(this).attr('id').split('-'),            type = post_data[0],            post_id = post_data[1];        $.ajax({            type: 'POST',            url: ot_admin_ajax.ajax_url,            data: {                action: 'dm_remove_image_options',                type: type,                post_id: post_id            },            success: function(data, textStatus, XMLHttpRequest) {                $('.' + type + '_image_preview').hide();                $('#' + type + '_image').val('')            },            error: function(MLHttpRequest, textStatus, errorThrown) {                alert(errorThrown)            }        })    })});